<template>
  <div class="login-container"  >
  <img class="bgImg" :src="`${cdmsBgSrc}`" alt="" style="width:100%;height:99%;">
  <!-- :style="`background:url(${videoSrc}) center/cover`" style="height:99%;"border:1px solid red;:style="`background:url(${cdmsBgSrc}) center/cover;`"-->
    <div class="handleVideo" >
      <!-- <img :src="videoSrc" alt="" style="width:100%;height:100%;"> -->
      <!-- <div style="position:absolute;top:18px;left:80px;width:510px;height:239px;z-index:99;border:1px solid red;"> -->
        <video src="http://139.198.0.141:82/pic/videologin.mp4" controls autoplay loop style="width:100%;"></video>
        <!-- <video src="http://www.chsl.ink/user/video.mp4" controls></video> -->
      <!-- </div> -->
    </div>
    <div class="from">
        <el-form autoComplete="on" :model="loginForm" :rules="loginRules" ref="loginForm" label-position="left" label-width="0px" class="card-box login-form">
          <h2 class="title" style="text-align:center;margin:0;">Colourdata Monitor System</h2>
          <h3 style="color:#ffffff;text-align:center;margin:10px;">水军违禁词系统</h3>
          <el-form-item prop="email">
            <span class="svg-container">
              <icon-svg icon-class="youxiang"></icon-svg>
            </span>
            <el-input name="email" type="text" v-model="loginForm.email" autoComplete="on" placeholder="邮箱"></el-input>
          </el-form-item>
          <el-form-item prop="password">
            <span class="svg-container">
              <icon-svg icon-class="mima"></icon-svg>
            </span>
            <el-input name="password" type="password" @keyup.enter.native="handleLogin" v-model="loginForm.password" autoComplete="on" placeholder="密码"></el-input>
          </el-form-item>
          <el-form-item>
            <el-button type="primary" style="width:100%;" :loading="loading" @click.native.prevent="handleLogin">
              登录
            </el-button>
          </el-form-item>
        </el-form>
    </div>
  </div>
</template>
<script>
import video from './../img/video.png';
import socialSign from './socialsignin';
import store from '../../store'
import cdmsbg from './../img/cdmsbg2.png'
// import videoLogin from './../img/videologin.mp4'
export default {
  components: { socialSign },
  name: 'login',
  data() {
    const validateEmail = (rule, value, callback) => {
      if (!value) {
        callback(new Error('请输入正确的合法邮箱'));
      } else {
        callback();
      }
    };
    const validatePass = (rule, value, callback) => {
      if (value.length < 6) {
        callback(new Error('密码不能小于6位'));
      } else {
        callback();
      }
    };
    return {
      videoSrc: video,
      cdmsBgSrc: cdmsbg,
      // videoLoginSrc: videologin,
      loginForm: {
        email: '',
        password: ''
      },
      loginRules: {
        email: [
          { required: true, trigger: 'blur', validator: validateEmail }
        ],
        password: [
          { required: true, trigger: 'blur', validator: validatePass }
        ]
      },
      loading: false,
      showDialog: false
    }
  },
  created() {
    // window.addEventListener('hashchange', this.afterQRScan);
  },
  destroyed() {
    // window.removeEventListener('hashchange', this.afterQRScan);
  },
  methods: {
    handleLogin() {
      this.$refs.loginForm.validate(valid => {
        if (valid) {
          this.loading = true;
          this.$store.dispatch('LoginByEmail', this.loginForm).then(() => {
            this.loading = false;
            console.log(store.state.user.code);
            if (store.state.user.code === 88888) {
              this.$router.push({ path: '/' });
              sessionStorage.setItem('carousel', 1)
              // console.log(store.state)
            } else {
              this.$message({
                message: '用户名或密码错误，请重试！',
                type: 'error'
              })
            }
          }).catch(() => {
            this.loading = false;
          });
        } else {
          console.log('error submit!!');
          return false;
        }
      });
    }
  }
}
</script>
<style rel="stylesheet/scss" lang="scss" >
@import "src/styles/mixin.scss";
.tips {
  font-size: 14px;
  color: #fff;
  margin-bottom: 5px;
}
.login-container {
  @include relative;
  height: 100vh;
  background-color: #333333;
  input:-webkit-autofill {
    -webkit-box-shadow: 0 0 0px 1000px #293444 inset !important;
    -webkit-text-fill-color: #fff !important;
  }
  input {
    background: transparent;
    border: 0px;
    -webkit-appearance: none;
    border-radius: 0px;
    padding: 12px 5px 12px 15px;
    color: #eeeeee;
    height: 47px;
  }
  .el-input {
    display: inline-block;
    height: 47px;
    width: 85%;
  }
  .svg-container {
    padding: 6px 5px 6px 15px;
    color: #889aa4;
  }
  .title {
    font-size: 20px;
    font-weight: 400;
    color: #eeeeee;
    margin: 0px auto 40px auto;
    text-align: center;
    font-weight: bold;
  }
  .login-form {
    position: absolute;
    left: 0;
    right: 0;
    width: 300px;
    padding: 0px 35px 15px 35px;
    margin: auto;
  }
  .el-form-item {
    border: 1px solid rgba(255, 255, 255, 0.1);
    background: rgba(0, 0, 0, 0.1);
    border-radius: 5px;
    color: #454545;
  }
  .forget-pwd {
    color: #fff;
  
  }
  .from{
    position: absolute;
    left: 68%;
    top: 30%;
  }
  .handleVideo{
    width: 778px;
    height: 350px;
    position: absolute;
    top: 25%;
    left: 8%;

  
  }
}
</style>